import Layout from "../components/layout";

import { gql, useQuery } from "@apollo/client";
import { useRouter } from "next/router";
import ClienteLista from "../components/cliente";
import Link from "next/link";

const OBTENER_CLIENTES_USUARIOS = gql`
  query obtenerClientesVendedor {
    obtenerClientesVendedor {
      id
      nombre
      apellido
      empresa
      email
    }
  }
`;

const columns = [
  { title: "nombre", field: "nombre" },
  { title: "empresa", field: "empresa" },
];

const Clientes = () => {
  const router = useRouter();
  this.state = {
    paginador: {
      offset: 0,
      actual: 1,
    },
  };
  const { data, loading, client } = useQuery(OBTENER_CLIENTES_USUARIOS);

  if (loading) {
    return (
      <div>
        <Layout>
          <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

          <Link href="/nuevocliente">
            <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
              Nuevo Cliente
            </a>
          </Link>
          <table className="table-auto shadow-md mt-10 w-full w-lg">
            <thead className="bg-gray-800">
              <tr className="text-white">
                <th className="w-1/5 py-2"> Nombre</th>
                <th className="w-1/5 py-2"> Empresa</th>
                <th className="w-1/5 py-2"> Email</th>
                <th className="w-1/5 py-2" colSpan="2">
                  Acción
                </th>
              </tr>
            </thead>
            <tbody className="bg-white">
              <tr>
                <th></th>
                <th></th>
              </tr>
            </tbody>
          </table>
        </Layout>
      </div>
    );
  }

  if (!data.obtenerClientesVendedor) {
    client.clearStore();
    router.push("/");
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Layout>
        <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

        <Link href="/nuevocliente">
          <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
            Nuevo Cliente
          </a>
        </Link>
        <table className="table-auto shadow-md mt-10 w-full w-lg">
          <thead className="bg-gray-800">
            <tr className="text-white">
              <th className="w-1/5 py-2"> Nombre</th>
              <th className="w-1/5 py-2"> Empresa</th>
              <th className="w-1/5 py-2"> Email</th>
              <th className="w-1/5 py-2" colspan="2">
                Acción
              </th>
            </tr>
          </thead>
          <tbody className="bg-white">
            {data.obtenerClientesVendedor.map((cliente) => (
              <ClienteLista key={cliente.id} cliente={cliente} />
            ))}
          </tbody>
        </table>
      </Layout>
    </div>
  );
};

export default Clientes;
