import Layout from "../components/layout";
import MaterialTable from "material-table";
import { gql, useQuery } from "@apollo/client";
import { useRouter } from "next/router";
import ClienteLista from "../components/cliente";
import Link from "next/link";

const OBTENER_CLIENTES_USUARIOS = gql`
  query obtenerClientesVendedor {
    obtenerClientesVendedor {
      id
      nombre
      apellido
      empresa
      email
    }
  }
`;

const useStyles = makeStyles((theme) => ({
  modal: {
    position: "absolute",
    width: 500,
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
  },
  iconos: {
    cursor: "pointer",
  },
  inputMaterial: {
    width: "100%",
  },
}));

const Clientes = () => {
  const router = useRouter();
  const styles = useStyles();
  const { data, loading, client } = useQuery(OBTENER_CLIENTES_USUARIOS);
  console.log(data);
  if (loading) {
    return <p>Loading...</p>;
  }

  if (!data.obtenerClientesVendedor) {
    client.clearStore();
    router.push("/");
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Layout>
        <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

        <Link href="/nuevocliente">
          <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
            Nuevo Cliente
          </a>
        </Link>
        <table className="table-auto shadow-md mt-10 w-full w-lg">
          <thead className="bg-gray-800">
            <tr className="text-white">
              <th className="w-1/5 py-2"> Nombre</th>
              <th className="w-1/5 py-2"> Empresa</th>
              <th className="w-1/5 py-2"> Email</th>
              <th className="w-1/5 py-2" colspan="2">
                Acci√≥n
              </th>
            </tr>
          </thead>
          <tbody className="bg-white">
            {data.obtenerClientesVendedor.map((cliente) => (
              <ClienteLista key={cliente.id} cliente={cliente} />
            ))}
          </tbody>
        </table>

        <MaterialTable
          columns={columns}
          data={data}
          title="Especialidades"
          options={{
            actionsColumnIndex: -1,
            exportButton: true,
            headerStyle: {
              backgroundColor: "#a5c422",
              color: "#FFF",
            },
          }}
          localization={{
            header: {
              actions: "Acciones",
            },
          }}
        />
      </Layout>
    </div>
  );
};

export default Clientes;
