import Layout from "../components/layout";
import React, { useState } from "react";
import { gql, useQuery } from "@apollo/client";
import { useRouter } from "next/router";
import ClienteLista from "../components/cliente";
import Link from "next/link";

const OBTENER_CLIENTES_USUARIOS = gql`
  query obtenerClientesVendedor {
    obtenerClientesVendedor {
      id
      nombre
      apellido
      empresa
      email
    }
  }
`;

const Clientes = () => {
  const router = useRouter();
  const { data, loading, client } = useQuery(OBTENER_CLIENTES_USUARIOS);

  var state = {
    querySet: data,

    page: 1,
    rows: 5,
    window: 5,
  };

  function pagination(querySet, page, rows) {
    var trimStart = (page - 1) * rows;
    var trimEnd = trimStart + rows;

    var trimmedData = querySet.slice(trimStart, trimEnd);

    var pages = Math.round(querySet.length / rows);

    return {
      querySet: trimmedData,
      pages: pages,
    };
  }

  function pageButtons(pages) {
    var wrapper = document.getElementById("pagination-wrapper");

    wrapper.innerHTML = ``;
    console.log("Pages:", pages);

    var maxLeft = state.page - Math.floor(state.window / 2);
    var maxRight = state.page + Math.floor(state.window / 2);

    if (maxLeft < 1) {
      maxLeft = 1;
      maxRight = state.window;
    }

    if (maxRight > pages) {
      maxLeft = pages - (state.window - 1);

      if (maxLeft < 1) {
        maxLeft = 1;
      }
      maxRight = pages;
    }

    for (var page = maxLeft; page <= maxRight; page++) {
      wrapper.innerHTML += `<button value=${page} class="page btn btn-sm btn-info">${page}</button>`;
    }

    if (state.page != 1) {
      wrapper.innerHTML =
        `<button value=${1} class="page btn btn-sm btn-info">&#171; First</button>` +
        wrapper.innerHTML;
    }

    if (state.page != pages) {
      wrapper.innerHTML += `<button value=${pages} class="page btn btn-sm btn-info">Last &#187;</button>`;
    }

    $(".page").on("click", function () {
      $("#table-body").empty();

      state.page = Number($(this).val());

      buildTable();
    });
  }

  if (loading) {
    return (
      <div>
        <Layout>
          <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

          <Link href="/nuevocliente">
            <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
              Nuevo Cliente
            </a>
          </Link>

          <table className="table-auto shadow-md mt-10 w-full w-lg">
            <thead className="bg-gray-800">
              <tr className="text-white">
                <th className="w-1/5 py-2"> Nombre</th>
                <th className="w-1/5 py-2"> Empresa</th>
                <th className="w-1/5 py-2"> Email</th>
                <th className="w-1/5 py-2" colSpan="2">
                  Acción
                </th>
              </tr>
            </thead>
            <tbody className="border border-light-blue-300 shadow rounded-md p-4 max-w-sm w-full mx-auto">
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
            </tbody>
          </table>
        </Layout>
      </div>
    );
  }

  if (!data.obtenerClientesVendedor) {
    client.clearStore();
    router.push("/");
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Layout>
        <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

        <Link href="/nuevocliente">
          <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
            Nuevo Cliente
          </a>
        </Link>
        <table className="table-auto shadow-md mt-10 w-full w-lg">
          <thead className="bg-gray-800">
            <tr className="text-white">
              <th className="w-0/5 py-2">Codigo</th>
              <th className="w-1/5 py-2"> Nombre</th>
              <th className="w-1/5 py-2"> Empresa</th>
              <th className="w-1/5 py-2"> Email</th>
              <th className="w-1/5 py-2" colSpan="2">
                Acción
              </th>
            </tr>
          </thead>
          <tbody className="bg-white" id="table-body">
            {data.obtenerClientesVendedor.map((cliente, index) => (
              <ClienteLista
                key={cliente.id}
                cliente={cliente}
                index={index + 1}
              />
            ))}
          </tbody>
        </table>
        <div class="container ">
          <div id="pagination-wrapper"></div>
        </div>
      </Layout>
    </div>
  );
};

export default Clientes;
