import Layout from "../components/layout";
import React, { useState, useEffect } from "react";
import { gql, useQuery } from "@apollo/client";
import { useRouter } from "next/router";
import ClienteLista from "../components/cliente";
import Link from "next/link";
import MaterialTable from "material-table";
const OBTENER_CLIENTES_USUARIOS = gql`
  query obtenerClientesVendedor {
    obtenerClientesVendedor {
      id
      nombre
      apellido
      empresa
      email
    }
  }
`;

const Clientess = () => {
  const router = useRouter();
  const { data, loading, client } = useQuery(OBTENER_CLIENTES_USUARIOS);

  if (loading) {
    return (
      <div>
        <Layout>
          <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

          <Link href="/nuevocliente">
            <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
              Nuevo Cliente
            </a>
          </Link>

          <table className="table-auto shadow-md mt-10 w-full w-lg">
            <thead className="bg-gray-800">
              <tr className="text-white">
                <th className="w-1/5 py-2"> Nombre</th>
                <th className="w-1/5 py-2"> Empresa</th>
                <th className="w-1/5 py-2"> Email</th>
                <th className="w-1/5 py-2" colSpan="2">
                  Acción
                </th>
              </tr>
            </thead>
            <tbody className="border border-light-blue-300 shadow rounded-md p-4 max-w-sm w-full mx-auto">
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
              <tr className="animate-pulse  space-x-4">
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
                <td>
                  <p className="h-4 bg-gray-400 rounded w-4/6 m-5 "></p>
                </td>
              </tr>
            </tbody>
          </table>
        </Layout>
      </div>
    );
  }

  if (!data.obtenerClientesVendedor) {
    client.clearStore();
    router.push("/");
    return <p>Loading...</p>;
  }

  var datos = data.obtenerClientesVendedor;
  console.log("datos", datos);

  const orders = useSelector((state) =>
    state.data.map((o) => ({ ...o, tableData: {} }))
  );

  console.log(" x", orders);

  var datos3 = [
    { name: "Mehmet", surname: "Baran", birthYear: 1987, birthCity: 63 },
    { name: "Zerya Betül", surname: "Baran", birthYear: 2017, birthCity: 34 },
  ];
  console.log("datos3", datos3);

  return (
    <div>
      <Layout>
        <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

        <Link href="/nuevocliente">
          <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase">
            Nuevo Cliente
          </a>
        </Link>
        <div className="overflow-x-scroll">
          <MaterialTable
            columns={[
              { title: "Nombre", field: "nombre" },
              { title: "Apellido", field: "apellido" },
              { title: "Empresa", field: "empresa" },
              { title: "Email", field: "email" },
            ]}
            data={datos}
            title="Demo Title"
          />
        </div>
      </Layout>
    </div>
  );
};

export default Clientess;
